/*Table structure for table `currencies` */

DROP TABLE IF EXISTS `currencies`;

CREATE TABLE `currencies` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `currency` varchar(3) NOT NULL,
  `fa_symbol` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `account_number` bigint(20) unsigned NOT NULL,
  `account_name` varchar(255) NOT NULL,
  `is_active` enum('Y','N') NOT NULL DEFAULT 'N',
  `usd_bid` varchar(255) NOT NULL,
  `usd_ask` varchar(255) NOT NULL,
  `name_en` varchar(255) NOT NULL,
  `name_es` varchar(255) NOT NULL,
  `name_ru` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `name_zh` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `name_kr` varchar(255) CHARACTER SET utf8 NOT NULL,
  PRIMARY KEY (`id`),
  KEY `currency` (`currency`),
  KEY `usd_bid` (`usd_bid`),
  KEY `usd_ask` (`usd_ask`)
